import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: '1 Choose one:',
			answerOptions: [
				{ answerText: 'smth1', isAnswerNumber: 1 },
				{ answerText: 'smth2', isAnswerNumber: 2 },
				{ answerText: 'smth3', isAnswerNumber: 3 },
				{ answerText: 'smth4', isAnswerNumber: 4 },
			],
		},
		{
			questionText: '2 Choose one:',
			answerOptions: [
				{ answerText: 'smth1', isAnswerNumber: 1 },
				{ answerText: 'smth2', isAnswerNumber: 2 },
				{ answerText: 'smth3', isAnswerNumber: 3 },
				{ answerText: 'smth4', isAnswerNumber: 4 },
			],
		},
		{
			questionText: '3 Choose one:',
			answerOptions: [
				{ answerText: 'smth1', isAnswerNumber: 1 },
				{ answerText: 'smth2', isAnswerNumber: 2 },
				{ answerText: 'smth3', isAnswerNumber: 3 },
				{ answerText: 'smth4', isAnswerNumber: 4 },
			],
		}
	];

	const [currentQuestion, setCurrentQuestion] = useState("");
	const [showScore, setShowScore] = useState("");
	const [score, setScore] = useState("");

	const handleAnswerOptionClick = (isAnswerNumber) => {
		setScore(score + toString(isAnswerNumber));
		

		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (
		<div className='app'>
			{showScore ? (
				<div className='score-section'>
					You scored {score} out of {questions.length}
				</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.isAnswerNumber)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}
		</div>
	);
}
